        rejectUnauthorized: false,
        withCredentials: true,
        crossdomain: true
    };

    const remoteOptions = {
        rejectUnauthorized: false,
        withCredentials: false,
        crossdomain: false
    };

    return new Promise((resolve,reject) => {

        axios.post("http://localhost:5000/user/login", {
                username: 'mclark',
                password: 'password'
            },
            localhostOptions
            )
                .then( (response) => {
                    console.log("response: " + JSON.stringify(response));
